mapper.select:

select reviewId, itemId, reviewTitle, reviewWriter, reviewContent, reviewImageURL,reviewRegdate, reviewUpdateDate from ( 
select /*+INDEX_DESC(tbl_review pk_review) */ rownum rn, reviewId, itemId, reviewTitle, reviewWriter, reviewContent, reviewImageURL,
reviewRegdate, reviewUpdateDate from tbl_review where 


rownum <= #{pageNum} * #{amount}
) where rn > (#{pageNum} -1) * #{amount}


SELECT * FROM(
    SELECT rownum rn, t.* FROM tbl_review t WHERE reviewWriter = #{memberId} ORDER BY reviewId DESC
) WHERE rownum <= #{pageNum} * #{amount}
AND rn > (#{pageNum} -1) * #{amount}
